package com.example.hydroagrosense.ui.navigation

import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.padding
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController
import com.example.hydroagrosense.ui.screens.Dashboard
import com.example.hydroagrosense.ui.screens.IrrigationDetails
import com.example.hydroagrosense.ui.screens.IrrigationHistory

@Composable
fun AppNavHost(
    navController: NavHostController = rememberNavController()
) {
    Column(modifier = Modifier.padding(0.dp)) {
        NavHost(
            navController = navController,
            startDestination = "dashboard"
        ) {
            composable("dashboard") { Dashboard() }
            composable("history") { IrrigationHistory() }
            composable("details") { IrrigationDetails() }
        }
    }
}

package com.example.hydroagrosense.ui.navigation

import androidx.compose.foundation.layout.Column
import androidx.compose.foundation.layout.padding
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController
import com.example.hydroagrosense.ui.screens.Dashboard
import com.example.hydroagrosense.ui.screens.IrrigationDetails
import com.example.hydroagrosense.ui.screens.IrrigationHistory

@Composable
fun AppNavHost(
    navController: NavHostController = rememberNavController()
) {
    Column(modifier = Modifier.padding(0.dp)) {
        NavHost(
            navController = navController,
            startDestination = "dashboard"
        ) {
            composable("dashboard") { Dashboard(navController) }
            composable("history") { IrrigationHistory(navController) }
            composable("details") { IrrigationDetails() }
        }
    }
}
